[templates.add save_release_note]

  [plot.release_note.add release_title='{{request.get release_title m.stripslashes_deep=yes}}' release_note='{{request.get release_note m.stripslashes_deep=yes}}' set="template.res" /]

   //** Activity log **//
  [template.set metas_act.activity_note="Release Note <em>(<a href='{app.path}/release-notes'>{request.release_title}</a>)</em> was added." /]
  [plot.activity.log object_id="{request.task_id}" coll_type="note" metas="{template.metas_act}" set="template.activity" /]


  //** set response message **//
    [if.equal lhs="{template.res.status}" rhs="success"]
      [template.set alert_class="alert-success" /]
      [template.set alert_msg="<strong>Success!</strong> Release note added." /]
      <script type="spa/axn" axn=page.update route_ajax='release-notes'></script> 
    [/if.equal]
    [if.equal  lhs="{template.res.status}" rhs="error"]
      [template.set alert_class="alert-danger" /]
      [template.set alert_msg="<strong>Error!</strong> {template.res.reason}. Try again." /]
    [/if.equal]

    <template axn=selector.update selector='div.feedback-res' get='me.html'>
    	[form_control.alert alert_class="{template.alert_class}" alert_msg="{template.alert_msg}" /]
    </template>  




[/templates.add]
